import { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import employeeService from '../services/employee.service';

const EmployeeList = () => {

  const [employees, setEmployees] = useState([]);

  const init = () => {
    employeeService.getAll()
      .then(response => {
        console.log('Printing employees data', response.data);
        setEmployees(response.data);
      })
      .catch(error => {
        console.log('Something went wrong', error);
      }) 
  }

  useEffect(() => {
    init();
  }, []);

  const handleDelete = (id) => {
    console.log('Printing id', id);

    ConfirmAlert({
      title:'Confirm to delete',
      message:'Are you sure',
      buttons:[
        {
          label:'Yes',
          onClick:()=>employeeService.remove(id)
          .then(response => {
            console.log('employee deleted successfully', response.data);
            init();
          })
          .catch(error => {
            console.log('Something went wrong', error);
          })
        }
      ]
    })



  }

  return (
    <div className="container">
      <h3>List of Employees</h3>
      <hr/>
      <div>
        <Link to="/add" className="btn btn-primary mb-2">Add Employee</Link>
        <table className="table table-bordered table-striped">
          <thead className="thead-dark">
            <tr>
              <th>Profile pic</th>
              <th>Name</th>
              <th>Location</th>
              <th>Department</th>
              <th>City</th>
              <th>Income</th>
              <th>Date of Joining</th>

              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
          {
            employees.map(employee => (
              <tr key={employee.id}>
                <td><img src={employee.url} alt="profle-pic" width="100" height="100"></img></td>
                <td>{employee.name}</td>
                <td>{employee.location}</td>
                <td>{employee.department}</td>
                <select>
                  <option value="Madras">Madras</option>
                  <option value="Chennai">Chennai</option>
                  <option selected value="Sahibganj">Sahibganj</option>
                  <option value="Hyderabad">Hyderabad</option>
                  <option value="Banglore">Banglore</option>
                </select>

                <td>{employee.income}</td>
                <td>{employee.doj}</td>
                <td>
                  <Link className="btn btn-info" to={`/employees/edit/${employee.id}`}>Update</Link>

                  <button className="btn btn-danger ml-2" onClick={() => {
                    handleDelete(employee.id);
                  }}>Delete</button>
                </td>
              </tr>
            ))
          }
          </tbody>
        </table>

      </div>
    </div>
  );
}

export default EmployeeList;
